import React, { useState, useEffect } from 'react';
import { nanoid } from 'nanoid';
import Todo from './components/Todo';
import Form from './components/Form';
import Header from './components/Header';
import FilterButton from './components/FilterButton';

import './App.css';

  function App(props) {

    const [tasks, setTasks] = useState([]);

    async function fetchTodoItems(uri) {
      const response = await fetch(uri);
      response.json()
      .then(response => setTasks(response))
      .catch(err => console.log(err));
    }

    async function addTask(content) {
      const newTask =  { name: content, completed: false }

      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newTask)
      };

        const response = await fetch(uri, requestOptions);
        response.json()
        .then(response => setTasks(response))
        .catch(err => console.log(err));
        console.log(response);

        setTasks([...tasks, newTask]);
    }

    // useEffect(() => {
    //   fetchTodoItems('http://localhost:5000/api/todoitems/');
    // },[]);

    const taskList = tasks.map(task => (
      <Todo 
          name={task.content}
          id={task.id}
          completed={task.isComplete}
          key={task.id}
          userName={task.userName}
          toggleTaskCompleted={toggleTaskCompleted}
          deleteTask={deleteTask}
          fetchTodos={fetchTodoItems}
          postTodo={postTodo}
          />
        )
      );

  const taskNoun = taskList.length > 1 ? 'tasks' : 'task'; 
  const remainingTasks = `${taskList.length}  ${taskNoun} remaining`;



  function toggleTaskCompleted(id) {
    const updatedTask = tasks.map(task => {
      if(id === task.id) {
        return {...task, completed: !task.completed}
      }
      return task;
    });
    setTasks(updatedTask);
  }

  function deleteTask(id) {
    const newTaskList = tasks.filter(task => id !== task.id);
    setTasks(newTaskList);
  }

  return (

    <div className="todoapp stack-large">
        <Header/>
        <Form addTask={addTask}/>
        <FilterButton name="All"/>
        <FilterButton name="Completed"/>
        <FilterButton name="To do"/>

        <h2 id="list-heading">
          {remainingTasks}
        </h2>
        <ul
          role="list"
          className="todo-list stack-large stack-exception"
          aria-labelledby="list-heading"
        >
          {taskList}

        </ul>
      </div>
    );
  }

export default App;
