import React, { useState, useRef, useEffect } from 'react';
import { nanoid } from 'nanoid';
import Todo from './components/Todo';
import Form from './components/Form';
import Header from './components/Header';
import FilterButton from './components/FilterButton';
import './App.css';

function usePrevious(value) {
	const ref = useRef();
	useEffect(() => {
	  ref.current = value;
	});
	return ref.current;
}

const URI = `http://localhost:5000/api/todoitems/`;

const FILTER_MAP = {
  All: () => true,
  Completed: task => task.isComplete,
  Todo: task => !task.isComplete
}

const FILTER_NAMES = Object.keys(FILTER_MAP);

function App(props) {

  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('All');
  const prevTaskLength = usePrevious(tasks.length);

  const filterList = FILTER_NAMES.map(name => (
    <FilterButton 
      key={name}
      name={name}
      isPressed={name == filter}
      setFilter={setFilter}
    />
  ));

  const taskList = tasks
    .filter(FILTER_MAP[filter])
    .map(task => (
      <Todo 
          content={task.content}
          title={task.title}
          completed={task.isComplete}
          id={task.id}
          key={task.id}
          toggleTaskCompleted={toggleTaskCompleted}
          deleteTask={deleteTask}
          editTask={editTask}
      />
    )
  );

  	const taskNoun = taskList.length > 1 ? 'tasks' : 'task'; 
  	const remainingTasks = `${taskList.length}  ${taskNoun} remaining`;

	async function fetchData() {
		const apiUrl = URI;
		const response = await fetch(apiUrl)
		const json = await response.json()
		console.log(json);
		setTasks(json);
	}

	async function addTask(content, title) {

		const newTask =  { id: 'todo-'+ nanoid(), title: title, content: content, completed: false }
		const response = await fetch(URI, {
			method: 'POST',
			mode: 'cors', 
			cache: 'no-cache',
			credentials: 'same-origin',
			headers: {
			'Content-Type': 'application/json'
			},
			redirect: 'follow',
			referrerPolicy: 'no-referrer', 
			body: JSON.stringify(newTask)
		});

			fetchData();
	}  

  	function toggleTaskCompleted(id) {
    	const updatedTask = tasks.map(task => {
      	if(id === task.id) {
		return {...task, completed: !task.completed}
		}
      return task;
	});
    setTasks(updatedTask);
  }

  function deleteTask(id) {
    const newTaskList = tasks.filter(task => id !== task.id);
    setTasks(newTaskList);
  }

  function editTask(id, newContent, newTitle) {
    const newTaskList = tasks.map(task => {
      if(id === task.id) {
        console.log({newContent, newTitle});
        return {...task, content: newContent, title: newTitle}
      }
      return task;
    });
    setTasks(newTaskList);
  }

  function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }

  const listHeadingRef = useRef(null);

  useEffect(() => {
	if (tasks.length - prevTaskLength === -1) {
	  listHeadingRef.current.focus();
	}
  }, [tasks.length, prevTaskLength]);

  useEffect(() => {
	fetchData();
  }, []);

  return (	
    <div className="todoapp stack-large">
        <Header/>
        <Form addTask={addTask}/>
        {filterList}
        <ul role="list" className="todo-list stack-large stack-exception" aria-labelledby="list-heading">
        <h2 id="list-heading" tabIndex="-1" ref={listHeadingRef}>
          {remainingTasks}
        </h2>
        {taskList}
        </ul>
      </div>
    );
  }

export default App;
