import React, { useState, useRef, useEffect } from 'react';
import Todo from './components/Todo';
import Form from './components/Form';
import Header from './components/Header';
import FilterButton from './components/FilterButton';
import './App.css';

const URI = `http://localhost:5000/api/todoitems/`;

const FILTER_MAP = {
  All: () => true,
  Completed: task => task.isComplete,
  Todo: task => !task.isComplete
}

const FILTER_NAMES = Object.keys(FILTER_MAP);

function App(props) {

  const [tasks, setTasks] = useState([]);
  const [taskById, setTaskById] = useState({});
  const [filter, setFilter] = useState('All');

  const prevTaskLength = usePrevious(tasks.length);

  const filterList = FILTER_NAMES.map(name => (
    <FilterButton 
      key={name}
      name={name}
      isPressed={name === filter}
      setFilter={setFilter}
    />
  ));

  const taskList = tasks
    .filter(FILTER_MAP[filter])
    .map(task => (
      <Todo 
          content={task.content}
          title={task.title}
          completed={task.isComplete}
          id={task.id}
          key={task.id}
          toggleTaskCompleted={toggleTaskCompleted}
          deleteTask={deleteTask}
          editTask={editTask}
      />
    )
  );

  	const taskNoun = taskList.length > 1 ? 'tasks' : 'task'; 
  	const remainingTasks = `${taskList.length}  ${taskNoun} remaining`;

	async function fetchTodos() {
		const apiUrl = URI;
		const response = await fetch(apiUrl)
		const json = await response.json()
		setTasks(json);
	}

	async function fetchTodoById(id) {
		const apiUrl = URI + id;
		const response = await fetch(apiUrl)
		const json = await response.json()
		var todo = { id: json.id, title: json.title, content: json.content, isComplete: !json.isComplete}
		setTaskById(todo);
	}

	async function addTask(content, title) {

		const newTask =  { title: title, content: content }
		const response = await fetch(URI, {
			method: 'POST',
			mode: 'cors', 
			cache: 'no-cache',
			credentials: 'same-origin',
			headers: {
			'Content-Type': 'application/json'
			},
			redirect: 'follow',
			referrerPolicy: 'no-referrer', 
			body: JSON.stringify(newTask)
		});
		console.log(response.json());
		fetchTodos();
	}  

  	async function toggleTaskCompleted(id) {
		 fetchTodoById(id);
		  console.log(JSON.stringify(taskById));
		console.log('Task by Id' + taskById)
		const response = await fetch(URI + id, {
			method: 'PUT', 
			mode: 'cors', 
			cache: 'no-cache',
			credentials: 'same-origin',
			headers: {
			 'Content-type': 'application/json; charset=UTF-8' 
			},
			body: JSON.stringify({taskById}) 
	});
	console.log(response.json());
	fetchTodos();
}

  async function deleteTask(id) {

	const response = await fetch(URI + id, {
		method: 'DELETE',
	});

	console.log(response.json());
	fetchTodos();
  }

  async function editTask(id, newTodo) {

	const updatedTask =  { id: id, title: newTodo.title, content: newTodo.content }
	console.log(JSON.stringify(updatedTask));
	const response = await fetch(URI + id, {
			method: 'PUT', 
			mode: 'cors', 
			cache: 'no-cache',
			credentials: 'same-origin',
			headers: {
			 'Content-type': 'application/json; charset=UTF-8' 
			},
			body: JSON.stringify(updatedTask) 

	});
	console.log(response.json());
	fetchTodos();
}

  function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }

  const listHeadingRef = useRef(null);

  useEffect(() => {
	if (tasks.length - prevTaskLength === -1) {
	  listHeadingRef.current.focus();
	}
  }, [tasks.length, prevTaskLength]);

  useEffect(() => {
	fetchTodos();
  }, []);

  return (	
    <div className="todoapp stack-large">
        <Header/>
        <Form addTask={addTask}/>
        {filterList}
        <ul role="list" className="todo-list stack-large stack-exception" aria-labelledby="list-heading">
        <h2 id="list-heading" tabIndex="-1" ref={listHeadingRef}>
          {remainingTasks}
        </h2>
        {taskList}
        </ul>
      </div>
    );
  }

export default App;
