import React, {useState, useEffect, useRef} from 'react';
import './Dropdown.scss';

export default function DropdownMenu({title, items, multiSelect}) {

	const [open, setOpen] = useState(false);
	const [selection, setSelection] = useState([]);

	const toggle = () => setOpen(!open);

	function handleOnClick(){}

	return (
		<div className="dd-wrapper">
		  <div
			tabIndex={0}
			className="dd-header"
			role="button"
			onKeyPress={() => toggle(!open)}
			onClick={() => toggle(!open)}
		  >
			<div className="dd-header__title">
			  <p className="dd-header__title">{title}</p>
			</div>
			<div className="dd-header__action dd-header__title--bold">
			  <p>{open ? 'Close' : 'Open'}</p>
			</div>
			{ open && (
				<ul className="dd-list">
					{items.map(item => (
						<li className="dd-list-item" key={item.id}>
							<button type="button" onClick={() => handleOnClick(item)}></button>
						</li>
					))}
				</ul>
			)

			}
		  </div>
		</div>
	);
}
